<script>
import {ref} from "vue";

export default {
    name: 'SignText',
    emits: ['onTap'],
    
    setup(_, {emit}) {
        let isClick = ref(false);
        const clickToSign = () => {
            isClick.value = !isClick.value;
            emit('onTap',{
                isClick: isClick.value,
            });
        }
        return {
            clickToSign,
        }
    }
}
</script>